mapper.select:
select count(case when code = #{code, jdbcType=VARCHAR} then 1 else null end) as codeCount
  from user_info
 where id <> #{id, jdbcType=VARCHAR}

mapper.update:
update user_info
set password = #{password, jdbcType=VARCHAR}
where id = #{id, jdbcType=VARCHAR}

